import React, { useEffect, useState } from "react";
import Youtube from "./assets/youtube.png";
import Delete from "./assets/delete.png";

function App() {
  const [value, setValue] = useState("");
  const [data, setData] = useState([]);
  const [submitting, setSubmitting] = useState(false);
  const [copiedIndex, setCopiedIndex] = useState(null);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!value.trim()) {
      alert("Please provide valid text to summarize.");
      return;
    }

    setSubmitting(true);
    setError(null);

    const requestOptions = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        contents: [{
          parts: [{
            text: `Provide a concise TL;DR summary of the following text:\n\n${value}`
          }]
        }]
      }),
    };

    try {
      const response = await fetch(
        `https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=${process.env.REACT_APP_GEMINI_KEY}`,
        requestOptions
      );

      if (!response.ok) {
        const errorBody = await response.text();
        throw new Error(`Failed to fetch summary: ${errorBody}`);
      }

      const dt = await response.json();

      const text = dt.candidates?.[0]?.content?.parts?.[0]?.text?.trim();
      if (!text) throw new Error("Invalid response from Gemini.");

      const updatedData = [text, ...data];
      setData(updatedData);

      // Use try-catch for localStorage to handle potential errors
      try {
        localStorage.setItem("summary", JSON.stringify(updatedData));
      } catch (storageError) {
        console.error("Failed to save to localStorage:", storageError);
      }
    } catch (err) {
      setError(err.message);
      console.error("Summarization error:", err);
    } finally {
      setSubmitting(false);
    }
  };

  const fetchLocalStorage = () => {
    try {
      const result = localStorage.getItem("summary");
      setData(result ? JSON.parse(result) : []);
    } catch (error) {
      console.error("Error reading localStorage:", error);
      setData([]);
    }
  };

  const copyTextToClipboard = async (text, index) => {
    try {
      await navigator.clipboard.writeText(text);
      setCopiedIndex(index);

      setTimeout(() => setCopiedIndex(null), 1500);
    } catch (err) {
      console.error("Failed to copy text:", err);
      alert("Could not copy text to clipboard.");
    }
  };

  const handleDelete = (txt) => {
    const filtered = data.filter((d) => d !== txt);
    setData(filtered);

    try {
      localStorage.setItem("summary", JSON.stringify(filtered));
    } catch (error) {
      console.error("Failed to update localStorage:", error);
    }
  };

  useEffect(() => {
    fetchLocalStorage();
  }, []);

  return (
    <div className="w-full bg-[#0f172a] h-full min-h-[100vh] py-4 px-4 md:px-20">
      <header className="flex justify-between items-center w-full h-10 px-5 2xl:px-40">
        <h3 className="cursor-pointer text-3xl font-bold text-cyan-600">
          Summary!
        </h3>
        <a
          href="https://youtube.com/@CodeWaveWithAsante"
          target="_blank"
          rel="noreferrer"
        >
          <img 
            src={Youtube} 
            alt="YouTube" 
            className="w-10 h-10 rounded-lg cursor-pointer" 
          />
        </a>
      </header>

      <main className="flex flex-col items-center justify-center mt-4 p-4">
        <h1 className="text-3xl text-white text-center leading-10 font-semibold">
          Summarizer with
          <br />
          <span className="text-5xl font-bold text-cyan-500">Google Gemini</span>
        </h1>
        <p className="mt-5 text-lg text-gray-500 sm:text-xl text-center max-w-2xl">
          Paste your text below to get a quick summary using Google Gemini!
        </p>

        <textarea
          placeholder="Paste text content here ..."
          rows={6}
          className="block w-full md:w-[650px] rounded-md border border-slate-700 bg-slate-800 p-2 text-sm shadow-lg font-medium text-white focus:border-gray-500 focus:outline-none focus:ring-0 mt-5"
          value={value}
          onChange={(e) => setValue(e.target.value)}
        ></textarea>

        {value.trim() && (
          <button
            className={`mt-5 bg-blue-500 px-5 py-2 text-white text-md font-medium cursor-pointer rounded-md ${
              submitting ? "opacity-50 cursor-not-allowed" : ""
            }`}
            onClick={handleSubmit}
            disabled={submitting}
          >
            {submitting ? "Please wait..." : "Submit"}
          </button>
        )}

        {error && <p className="text-red-500 mt-3">{error}</p>}
      </main>

      {data.length > 0 && (
        <section className="w-full mt-10 flex flex-col gap-5 shadow-md items-center justify-center">
          <p className="text-white font-semibold text-lg">Summary History</p>
          {data.map((d, index) => (
            <div key={index} className="max-w-2xl bg-slate-800 p-3 rounded-md">
              <p className="text-gray-400 text-lg">{d}</p>
              <div className="flex gap-5 items-center justify-end mt-2">
                <p
                  className="text-gray-500 font-semibold cursor-pointer"
                  onClick={() => copyTextToClipboard(d, index)}
                >
                  {copiedIndex === index ? "Copied" : "Copy"}
                </p>
                <span
                  className="cursor-pointer"
                  onClick={() => handleDelete(d)}
                >
                  <img src={Delete} alt="Delete" className="w-6 h-6" />
                </span>
              </div>
            </div>
          ))}
        </section>
      )}
    </div>
  );
}

export default App;
