import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [value, setValue] = useState("");
  const [questionsAndAnswers, setQuestionsAndAnswers] = useState([]);
  const [flashcards, setFlashcards] = useState([]);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState(null);

  const generateQuestionsAndAnswers = async () => {
    setSubmitting(true);
    setError(null);

    const requestOptions = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        contents: [{
          parts: [{
            text: `Extract the main points from the following text and turn them into questions with corresponding answers:\n\n${value}`
          }]
        }]
      }),
    };

    try {
      const response = await fetch(
        `https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=`,
        requestOptions
      );

      if (!response.ok) {
        const errorBody = await response.text();
        throw new Error(`Failed to fetch questions and answers: ${errorBody}`);
      }

      const data = await response.json();

      // Simulated response mapping for demonstration
      const questionsAndAnswers = data.candidates?.[0]?.content?.parts?.[0]?.text?.split("\n")
        .map(line => {
          const [question, answer] = line.split("?");
          return { question: question.trim() + "?", answer: answer.trim() };
        });

      setQuestionsAndAnswers(questionsAndAnswers || []);
    } catch (err) {
      setError("Failed to generate questions and answers");
    } finally {
      setSubmitting(false);
    }
  };

  const saveFlashcard = async (question, answer) => {
    try {
      const response = await axios.post("http://localhost:5000/flashcards", {
        question,
        answer,
      });
      setFlashcards([...flashcards, response.data]);
    } catch (err) {
      console.error("Failed to save flashcard:", err);
    }
  };

  const fetchFlashcards = async () => {
    try {
      const response = await axios.get("http://localhost:5000/flashcards");
      setFlashcards(response.data);
    } catch (err) {
      console.error("Failed to fetch flashcards:", err);
    }
  };

  const deleteFlashcard = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/flashcards/${id}`);
      setFlashcards(flashcards.filter((card) => card._id !== id));
    } catch (err) {
      console.error("Failed to delete flashcard:", err);
    }
  };

  useEffect(() => {
    fetchFlashcards();
  }, []);

  return (
    <div className="w-full bg-[#0f172a] h-full min-h-[100vh] py-4 px-4 md:px-20">
      <header className="flex justify-between items-center w-full h-10 px-5 2xl:px-40">
        <h3 className="cursor-pointer text-3xl font-bold text-cyan-600">
          Flashcard App
        </h3>
      </header>

      <main className="flex flex-col items-center justify-center mt-4 p-4">
        <h1 className="text-3xl text-white text-center font-semibold">
          Generate Flashcards
        </h1>
        <textarea
          placeholder="Paste your text here..."
          rows={6}
          className="block w-full md:w-[650px] rounded-md border border-slate-700 bg-slate-800 p-2 text-sm shadow-lg font-medium text-white focus:outline-none mt-5"
          value={value}
          onChange={(e) => setValue(e.target.value)}
        ></textarea>
        <button
          className={`mt-5 bg-blue-500 px-5 py-2 text-white text-md font-medium cursor-pointer rounded-md ${
            submitting ? "opacity-50 cursor-not-allowed" : ""
          }`}
          onClick={generateQuestionsAndAnswers}
          disabled={submitting}
        >
          {submitting ? "Generating..." : "Generate"}
        </button>
        {error && <p className="text-red-500 mt-3">{error}</p>}
      </main>

      {questionsAndAnswers.length > 0 && (
        <section className="w-full mt-10 flex flex-col gap-5 shadow-md items-center justify-center">
          <h2 className="text-lg text-white">Generated Questions</h2>
          {questionsAndAnswers.map(({ question, answer }, index) => (
            <div key={index} className="max-w-2xl bg-slate-800 p-3 rounded-md w-full">
              <p className="text-gray-400 text-lg"><strong>Q:</strong> {question}</p>
              <p className="text-gray-400 text-lg"><strong>A:</strong> {answer}</p>
              <button
                className="text-green-500 font-semibold mt-2"
                onClick={() => saveFlashcard(question, answer)}
              >
                Save as Flashcard
              </button>
            </div>
          ))}
        </section>
      )}

      {flashcards.length > 0 && (
        <section className="w-full mt-10 flex flex-col gap-5 shadow-md items-center justify-center">
          <h2 className="text-lg text-white">Saved Flashcards</h2>
          {flashcards.map((card) => (
            <div key={card._id} className="max-w-2xl bg-slate-700 p-3 rounded-md w-full">
              <p className="text-gray-300 text-lg"><strong>Q:</strong> {card.question}</p>
              <p className="text-gray-300 text-lg"><strong>A:</strong> {card.answer}</p>
              <button
                className="text-red-500 font-semibold mt-2"
                onClick={() => deleteFlashcard(card._id)}
              >
                Delete
              </button>
            </div>
          ))}
        </section>
      )}
    </div>
  );
}

export default App;
